import{r as e,h as n,k as i,F as r}from"./p-1bafedb0.js";import{t as s}from"./p-b2c1ccc4.js";const t=":host{display:block}";const a=6;const l=class{constructor(n){e(this,n)}render(){return n(r,null,n("app-header",{headerTitle:s[a]}),n("ion-content",{class:"ion-padding",id:"main-menu"},n("app-content",null,n("h1",{class:"ion-hide-md-down"},s[a]),n("p",{class:"lead"},"Konservativ, aber kein Elsässer: ",n("br",null),"Arthur Cohn wird erster Rabbiner in Basel"),n("p",null,"Die Wahl des ersten eigenen Rabbiners für die jüdische Gemeinde in Basel verlief kompliziert. 1883 trat der liberale Moise Nordmann, der als Rabbiner von Hégenheim aus auch für die Basler Juden zuständig war, zurück. Die jüdische Gemeinde in Basel wollte einen orthodox eingestellten Nachfolger, konnte sich aber lange auf keinen Kandidaten einigen. Obwohl sich die jüdische Gemeinde fast ausschliesslich aus Jüdinnen und Juden mit elsässischem Hintergrund zusammensetzte, fiel die Wahl schliesslich 1885 auf Arthur Cohn, der aus dem damals preussischen und heute polnischen Flatow stammte und in Berlin studiert hatte. Cohn hatte 1884 bei Theodor Mommsen, der 1902 für seine Arbeit zur Geschichte des antiken Roms den Literaturnobelpreis erhielt, promoviert und 1885 das Diplom des Rabbinerseminars in Berlin erhalten."),n("app-img",{src:"../../assets/teil-6/arthur-cohn.png"},"Das Porträt zeigt Rabbiner Arthur Cohn (1862-1926) ein Jahr vor seinem Tod. ",n("br",null),"Quelle: UBH Portr BS Cohn A 1862, 1"),n("p",null,"Arthur Cohn war kein Elsässer, sorgte mit seiner konservativen Haltung aber dafür, dass liberale Reformbewegungen in der jüdischen Gemeinde in Basel nicht nachhaltig erfolgreich blieben. Der Charakter der Basler Gemeinde war deshalb im Gegensatz zu vielen liberalen jüdischen Gemeinden in Deutschland orthodox geprägt. Während seiner Amtszeit gründete Cohn Vereine und Institutionen, die sich dem Studium der jüdischen Religion widmeten, weihte 1892 die vergrösserte Synagoge ein und eröffnete 1903 den neuen jüdischen Friedhof."),n("p",null,"Nach über vierzigjährigem Wirken verstarb Arthur Cohn 1926 im Amt. Auf ihn folgte Arthur Weil, der aus Frankreich stammte, sein Diplom aber am gleichen Rabbinerseminar wie sein Vorgänger erworben hatte."),n("h2",null,"Rätsel"),n("p",null,"Rabbiner Arthur Cohn hat einen «berühmten»"," ",n("a",{href:"https://hls-dhs-dss.ch/de/articles/046832/2003-12-18/",target:"_blank",rel:"noopener noreferrer"},"Enkel")," ",", der ein Jahr nach seinem Tod auf die Welt kam und den gleichen Namen trägt."," "),n("p",null,"Mit welcher Tätigkeit stand Cohns Enkel in der Öffentlichkeit?"),n("p",null,n("ion-button",{color:"primary",onClick:e=>this.successModal.open()},"Filmproduzent"),n("ion-button",{color:"primary",onClick:e=>this.failModal.open()},"Modedesigner"),n("ion-button",{color:"primary",onClick:e=>this.failModal.open()},"Luftfahrtpionier")),n("app-literatur",{items:[n(r,null,"Guth Biasini, Nadia: Arthur Cohn, der Rabbiner der Basler Gemeinde, in: Haumann, Heiko (Hg.): Der Erste Zionistenkongress von 1897 – Ursachen, Bedeutung, Aktualität, Basel 1997, S. 168."),n(r,null,"Draeger, Urs: Die Israelitische Gemeinde Basel 1880-1933: soziale Struktur, Organisation und Konflikte einer Einheitsgemeinde. Lizentiatsarbeit, Universität Freiburg/Schweiz, Freiburg 2001.")]}))),n("app-dialog-fail",{ref:e=>this.failModal=e,titleTxt:"Leider falsch"},n("p",null,"Die richtige Antwort ist «Filmproduzent»")),n("app-dialog-success",{nr:a,ref:e=>this.successModal=e},n("ion-img",{src:i("../../assets/hurray.gif")}),n("div",{class:"ion-padding"},"Du hast Kapitel «",s[a],"» erfolgreich abgeschlossen !!")))}};l.style=t;export{l as page_teil_6};
